import { GoogleGenAI, Type } from "@google/genai";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

export const getSmartQuoteAdvice = async (userMessage: string, lang: string) => {
  try {
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: `You are a professional assistant for 'Shree Ganesh Electricals & Infra Co.', a government-registered electrical contractor in Bhadravati, Maharashtra. 
      The user is asking for a quote or project advice: "${userMessage}".
      Respond in ${lang === 'mr' ? 'Marathi' : lang === 'hi' ? 'Hindi' : 'English'}.
      Be professional, trust-building, and suggest which of our services (HT/LT line, Transformer, Wiring, Substation, Material Supply) fits their need.
      Mention that Engineer Rupesh Thawas and his team will review the details soon. Keep it under 100 words.`,
      config: {
        temperature: 0.7,
      },
    });
    return response.text;
  } catch (error) {
    console.error("Gemini Error:", error);
    return null;
  }
};

import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu, X, Phone, MessageCircle, Globe } from 'lucide-react';
import { Language, TranslationStrings } from '../types';
import { LANGUAGES, TRANSLATIONS } from '../constants';

interface LayoutProps {
  children: React.ReactNode;
  lang: Language;
  setLang: (l: Language) => void;
}

const Navbar: React.FC<LayoutProps> = ({ lang, setLang }) => {
  const [isOpen, setIsOpen] = useState(false);
  const t = TRANSLATIONS[lang];
  const location = useLocation();

  const navLinks = [
    { path: '/', label: t.nav.home },
    { path: '/about', label: t.nav.about },
    { path: '/services', label: t.nav.services },
    { path: '/certifications', label: t.nav.certs },
    { path: '/contact', label: t.nav.contact },
  ];

  return (
    <nav className="bg-white border-b border-gray-100 sticky top-0 z-50 shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-20">
          <div className="flex items-center">
            <Link to="/" className="flex flex-col">
              <span className="text-red-700 font-bold text-xl leading-tight devanagari">
                {t.companyNameRegional}
              </span>
              <span className="text-gray-500 text-xs font-semibold uppercase tracking-wider">
                {t.companyName}
              </span>
            </Link>
          </div>

          {/* Desktop Nav */}
          <div className="hidden md:flex items-center space-x-8">
            {navLinks.map((link) => (
              <Link
                key={link.path}
                to={link.path}
                className={`${
                  location.pathname === link.path ? 'text-red-700' : 'text-gray-600'
                } hover:text-red-600 font-medium transition-colors devanagari`}
              >
                {link.label}
              </Link>
            ))}

            <div className="flex items-center space-x-2 border-l pl-8 ml-4">
              <Globe className="w-4 h-4 text-gray-400" />
              {LANGUAGES.map((l) => (
                <button
                  key={l.code}
                  onClick={() => setLang(l.code)}
                  className={`text-xs px-2 py-1 rounded ${
                    lang === l.code ? 'bg-red-700 text-white' : 'hover:bg-gray-100 text-gray-600'
                  }`}
                >
                  {l.label}
                </button>
              ))}
            </div>
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center space-x-4">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-gray-600 hover:text-red-700 p-2"
            >
              {isOpen ? <X /> : <Menu />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Nav */}
      {isOpen && (
        <div className="md:hidden bg-white border-t border-gray-100 pb-4">
          <div className="px-2 pt-2 pb-3 space-y-1">
            {navLinks.map((link) => (
              <Link
                key={link.path}
                to={link.path}
                onClick={() => setIsOpen(false)}
                className="block px-3 py-2 text-base font-medium text-gray-700 hover:text-red-700 hover:bg-gray-50 rounded-md devanagari"
              >
                {link.label}
              </Link>
            ))}
          </div>
          <div className="px-5 py-3 border-t border-gray-100 flex items-center space-x-2">
            <Globe className="w-4 h-4 text-gray-400" />
            {LANGUAGES.map((l) => (
