                key={l.code}
                onClick={() => {
                  setLang(l.code);
                  setIsOpen(false);
                }}
                className={`text-sm px-3 py-1 rounded ${
                  lang === l.code ? 'bg-red-700 text-white' : 'bg-gray-50 text-gray-600'
                }`}
              >
                {l.label}
